def render_admin_login(
    request: Request,
    error: Optional[str] = None,
    status_code: int = 200,
) -> Any:
    return _templates.TemplateResponse(
        "admin/login.html",
        {"request": request, "title": "CTXCE Admin Login", "error": error},
        status_code=status_code,
    )

