def render_admin_error(
    request: Request,
    title: str,
    message: str,
    back_href: str = "/admin",
    status_code: int = 400,
) -> Any:
    return _templates.TemplateResponse(
        "admin/error.html",
        {
            "request": request,
            "title": title,
            "message": message,
            "back_href": back_href,
        },
        status_code=status_code,
    )
