pub fn new(
    location: Loc,
    message: impl Into<String>,
    severity: DiagnosticSeverity,
    error_code: ErrorCode,
    hint: Option<String>,
    fix: Option<Fix>,
) -> Self {
    let filepath = location.filepath.clone();
    Self {
        location,
        message: message.into(),
        error_code,
        hint,
        fix,
        filepath,
        severity,
    }
}

