pub struct Span {
    pub line: usize,
    pub column: usize,
}

method_new_19.content:
pub fn new(line: usize, column: usize) -> Self {
    Self {
        line,
        column: column + 1,
    }
}

method_from_pos_26.content:
    pub fn from_pos(pos: &Position) -> Self {
        let (line, column) = pos.line_col();
        Self {
            line,
            column: column + 1,
        }
    }
}

method_new_36.content:
pub fn new(filepath: Option<String>, start: Span, end: Span, span: String) -> Self {
    Self {
        filepath,
        start,
        end,
        span,
    }
}

method_empty_45.content:
    pub fn empty() -> Self {
        Self::new(None, Span::new(1, 1), Span::new(1, 1), "".to_string())
    }
}

method_loc_with_filepath_53.content:
    fn loc_with_filepath(&self, filepath: String) -> Loc {
        Loc::new(
            Some(filepath),
            self.loc().start,
            self.loc().end,
            self.loc().span,
        )
    }
}

method_loc_63.content:
    fn loc(&self) -> Loc {
        Loc::new(
            None,
            Span::from_pos(&self.as_span().start_pos()),
            Span::from_pos(&self.as_span().end_pos()),
            self.as_span().as_str().to_string(),
        )
    }
}
