function_getTimeoutFromEnv_45.content:
func getTimeoutFromEnv(envVar string, defaultValue time.Duration) time.Duration {
	if s := os.Getenv(envVar); s != "" {
		if d, err := time.ParseDuration(s); err == nil {
			return d
		}
	}
	return defaultValue
}
