            impl ErrorCode {
                #[allow(unused)]
                #[allow(non_snake_case)]
                pub fn [<$error_code _message>]($($message_args: &str),*) -> String {
                    format!($message, $($message_args),*)
                }

                #[allow(unused)]
                #[allow(non_snake_case)]
                pub fn [<$error_code _hint>]($($hint_args: &str),*) -> String {
                    format!($hint, $($hint_args),*)
                }
            }
        }
    };
}

