// Contains checks if a string contains any of the substrings

function_contains_310.content:
func contains(s string, substrings []string) bool {
	for _, substr := range substrings {
		if len(s) >= len(substr) && s[:len(substr)] == substr {
			return true
		}
	}
	return false
}

// ValidateChatRequest validates a chat completion request

function_ValidateChatRequest_320.content:
func ValidateChatRequest(req ChatCompletionRequest) error {
	if req.Model == "" {
		return &APIError{
			Code:    ErrInvalidRequest,
			Message: "model is required",
		}
	}

	if len(req.Messages) == 0 {
		return &APIError{
			Code:    ErrInvalidRequest,
			Message: "at least one message is required",
		}
	}

	if req.MaxTokens != nil && *req.MaxTokens < 0 {
		return &APIError{
			Code:    ErrInvalidRequest,
			Message: "max_tokens must be non-negative",
		}
	}

	if req.Temperature != nil {
		if *req.Temperature < 0 || *req.Temperature > 2 {
			return &APIError{
				Code:    ErrInvalidRequest,
				Message: "temperature must be between 0 and 2",
			}
		}
	}

	if req.TopP != nil {
		if *req.TopP < 0 || *req.TopP > 1 {
			return &APIError{
				Code:    ErrInvalidRequest,
				Message: "top_p must be between 0 and 1",
			}
		}
	}

	return nil
}

// IsRetryableError checks if an error should be retried

function_IsRetryableError_364.content:
func IsRetryableError(err error) bool {
	if apiErr, ok := err.(*APIError); ok {
		switch apiErr.Code {
		case ErrRateLimited, ErrServerError:
			return true
		default:
			return false
		}
	}
	return false
}

