protected parseIntParam(req: Request, res: Response, paramName: string): number | null {
  const value = parseInt(req.params[paramName], 10);
  if (isNaN(value)) {
    this.badRequest(res, `Invalid ${paramName}`);
    return null;
  }
  return value;
}

