protected validateRequired(req: Request, res: Response, params: string[]): boolean {
  for (const param of params) {
    if (req.body[param] === undefined || req.body[param] === null) {
      this.badRequest(res, `Missing ${param}`);
      return false;
    }
  }
  return true;
}

