async function batchTranslation(): Promise<void> {
  const readmes = [
    "./README.md",
    "./packages/core/README.md",
    "./packages/cli/README.md",
  ];

  const languages = ["es", "fr", "de"];

  for (const readme of readmes) {
    console.log(`\nProcessing: ${readme}`);
    await translateReadme({
      source: readme,
      languages,
      verbose: true,
    });
  }
}

