func SetupTempTestDir(t *testing.T, files map[string]string) string {
	t.Helper()

	dir := t.TempDir()

	for path, content := range files {
		fullPath := dir + "/" + path
		err := os.WriteFile(fullPath, []byte(content), 0644)
		if err != nil {
			t.Fatalf("Failed to create test file %s: %v", fullPath, err)
		}
	}

	return dir
}

