pub trait RangeAdapter<'db, 'arena, 'txn>: Iterator {
    /// Range returns a slice of the current step between two points
    ///
    /// # Arguments
    ///
    /// * `start` - The starting index
    /// * `end` - The ending index
    ///
    /// # Example
    ///
    /// ```rust
    /// let traversal = G::new(storage, &txn).range(0, 10);
    /// ```
    fn range<N, K>(
        self,
        start: N,
        end: K,
    ) -> RoTraversalIterator<
        'db,
        'arena,
        'txn,

